import { useEffect, useState } from "react";
import apiRequests from "./apiRequests"
import{useQuery} from "react-query";
import React from 'react';
import apiDetails from "./product pages/apidetails";


export function Landing(){
    const [produqtebi, setProduqtebi] = useState([])

    const {data, isLoading} = useQuery("products", () => apiRequests('GET', "products")); 

    console.log(data);

    useEffect(() =>{
        if(data){
            setProduqtebi(data)
        }
    },[data])


    return <div >

       {isLoading ? <div>loader</div> : (
        <div className='container-products'>
             {
            produqtebi.map(item =>( 
                <div key={item.id}  className="div-items"> 
                   <img src={item.image}>  </img>
                   <h4> <Link to={"/product/"+ item.title }href=""> {item.title}</Link>  </h4> 
                   <p> <Link to={"/product/"+ item.description }href=""> {item.description}</Link>    </p> 
                   <h4> <Link to={"/product/"+ item.price }href=""> {item.price}</Link></h4>          

                </div>   
            ))
            }
        </div>
       )}


    </div>
}

export default Landing;
