export function Products(){
    const param = useParams()
    const {data, isLoading} = useQuery(["product" , param.productId], () => apiDetails("GET",  param.productId))
    console.log(data);

    useEffect(() =>{
      if(data){
          setProduqtebi(data)
      }
  },[data])

